import React, { Component } from 'react'
import Trytune from './trytune.js'

class Options extends Component {
  constructor(props) {
    super(props)
    this.state = {
      playtime: '5',
      fundamental: '440',
      shape: 'sine',
      root: '0',
      rootBNS: '0',
      triad: '[4,7]',
      seventh: 'none',
      ninth: 'none',
      eleventh: 'none',
      thirteenth: 'none',
      audio: null
    }
    this.changePlayTime = this.changePlayTime.bind(this);
    this.changeFundamental = this.changeFundamental.bind(this);
    this.changeShape = this.changeShape.bind(this);
    this.changeRoot = this.changeRoot.bind(this);
    this.changeRootBNS = this.changeRootBNS.bind(this);
    this.changeTriad = this.changeTriad.bind(this);
    this.changeSeventh = this.changeSeventh.bind(this);
    this.changeNinth = this.changeNinth.bind(this);
    this.changeEleventh = this.changeEleventh.bind(this);
    this.changeThirteenth = this.changeThirteenth.bind(this);
  }

  componentDidMount() {
    this.setState({audio: Trytune.getAudio})
  }

  changePlayTime(e) {
    let ops = this.state
    ops.playtime = e.target.value
    let audio = Trytune.getAudio(ops)
    this.setState({
      playtime: e.target.value,
      audio: audio
    })
    console.log(this.state.audio)
  }
  changeFundamental(e) {
    let ops = this.state
    ops.fundamental = e.target.value
    let audio = Trytune.getAudio(ops)
    this.setState({
      fundamental: e.target.value,
      audio: audio
    })
    console.log(this.state.audio)
  }
  changeShape(e) {
    let ops = this.state
    ops.shape = e.target.value
    let audio = Trytune.getAudio(ops)
    this.setState({
      shape: e.target.value,
      audio: audio
    })
    console.log(this.state.audio)
  }
  changeRoot(e) {
    let ops = this.state
    ops.root = e.target.value
    let audio = Trytune.getAudio(ops)
    this.setState({
      root: e.target.value,
      audio: audio
    })
    console.log(this.state.audio)
  }
  changeRootBNS(e) {
    let ops = this.state
    ops.rootBNS = e.target.value
    let audio = Trytune.getAudio(ops)
    this.setState({
      rootBNS: e.target.value,
      audio: audio
    })
    console.log(this.state.audio)
  }
  changeTriad(e) {
    let ops = this.state
    ops.triad = e.target.value
