import React, { Component } from 'react'
import Trytune from './trytune.js'

class Options extends Component {
  constructor(props) {
    super(props)
    this.state = {
      playtime: '5',
      fundamental: '440',
      shape: 'sine',
      root: '0',
      rootBNS: '0',
      triad: '[4,7]',
      seventh: 'none',
      ninth: 'none',
      eleventh: 'none',
      thirteenth: 'none',
      audio: null
    }
    this.changePlayTime = this.changePlayTime.bind(this);
    this.changeFundamental = this.changeFundamental.bind(this);
    this.changeShape = this.changeShape.bind(this);
    this.changeRoot = this.changeRoot.bind(this);
    this.changeRootBNS = this.changeRootBNS.bind(this);
    this.changeTriad = this.changeTriad.bind(this);
    this.changeSeventh = this.changeSeventh.bind(this);
    this.changeNinth = this.changeNinth.bind(this);
    this.changeEleventh = this.changeEleventh.bind(this);
    this.changeThirteenth = this.changeThirteenth.bind(this);
  }

  componentDidMount() {
    this.setState({audio: Trytune.updateAudio(this.state)})

  }

  updateAudio() {
    this.setState({audio: Trytune.updateAudio(this.state)})
    console.log(this.state.audio)
  }

  changePlayTime(e) {
    this.setState({playtime: e.target.value})
    this.updateAudio()
  }
  changeFundamental(e) {
    this.setState({fundamental: e.target.value})
    this.updateAudio()
  }
  changeShape(e) {
    this.setState({shape: e.target.value})
    this.updateAudio()
  }
  changeRoot(e) {
    this.setState({root: e.target.value})
    this.updateAudio()
  }
  changeRootBNS(e) {
    this.setState({rootBNS: e.target.value})
    this.updateAudio()
  }
  changeTriad(e) {
    this.setState({triad: e.target.value})
    this.updateAudio()
  }
  changeSeventh(e) {
    this.setState({seventh: e.target.value})
    this.updateAudio()
  }
  changeNinth(e) {
    this.setState({ninth: e.target.value})
    this.updateAudio()
  }
  changeEleventh(e) {
    this.setState({eleventh: e.target.value})
    this.updateAudio()
  }
  changeThirteenth(e) {
    this.setState({thirteenth: e.target.value})
    this.updateAudio()
  }

  render() {
    return (
      <div>
        <PlayTime
        value={this.state.playtime}
        change={this.changePlayTime}
        />
        <br/>
        <Fundamental
        value={this.state.fundamental}
        change={this.changeFundamental}
        />
        <br/>
        <Shape
        value={this.state.shape}
        change={this.changeShape}
        />
        <br/>
        <Root
        value={this.state.root}
        change={this.changeRoot}
        />
        <RootBNS
        value={this.state.rootBNS}
        change={this.changeRootBNS}
        />
        <br/>
        <Triad
        value={this.state.triad}
        change={this.changeTriad}
        />
        <br/>
        <Seventh
        value={this.state.seventh}
        change={this.changeSeventh}
        />
        <br/>
        <Ninth
        value={this.state.ninth}
        change={this.changeNinth}
        />
        <br/>
        <Eleventh
        value={this.state.eleventh}
        change={this.changeEleventh}
        />
        <br/>
        <Thirteenth
        value={this.state.thirteenth}
        change={this.changeThirteenth}
        />
        <br/>
        <div onClick={() => console.log(this.state)}>Log State</div>
        <br/>
        <button onClick={() => Trytune.play(Trytune.updateAudio(this.state), this.state.playtime, this.state.shape)}>Play</button>
      </div>
    )
  }
}

function PlayTime(props) {
  return (
