import React, { Component } from 'react'
import Trytune from './trytune.js'

class Options extends Component {
  constructor(props) {
    super(props)
    this.state = {
      playtime: '5',
      fundamental: '440',
      shape: 'sine',
      root: '0',
      rootBNS: '0',
      triad: '[4,7]',
      seventh: 'none',
      ninth: 'none',
      eleventh: 'none',
      thirteenth: 'none',
      audio: null
    }
  }

  componentDidMount() {
    this.setState({audio: Trytune.updateAudio(this.state)})
    console.log(this.state.audio)
  }

  changePlayTime = (e) => this.setState({playtime: e.target.value})
  changeFundamental = (e) => this.setState({fundamental: e.target.value})
  changeShape = (e) => this.setState({shape: e.target.value})
  changeRoot = (e) => this.setState({root: e.target.value})
  changeRootBNS = (e) => this.setState({rootBNS: e.target.value})
  changeTriad = (e) => this.setState({triad: e.target.value})
  changeSeventh =
  changeNinth =
  changeEleventh =
  changeThirteenth = (e) => this.setState({thirteenth: e.target.value})

  render() {
    return (
      <div>
        <PlayTime
        value={this.state.playtime}
        change={this.changePlayTime(e)}
        />
        <br/>
        <Fundamental
        value={this.state.fundamental}
        change={this.changeFundamental(e)}
        />
        <br/>
        <Shape
        value={this.state.shape}
        change={this.changeShape(e)}
        />
        <br/>
        <Root
        value={this.state.root}
        change={this.changeRoot(e)}
        />
        <RootBNS
        value={this.state.rootBNS}
        change={this.changeRootBNS(e)}
        />
        <br/>
        <Triad
        value={this.state.triad}
        change={this.changeTriad(e)}
        />
        <br/>
        <Seventh
        value={this.state.seventh}
        change={(e) => this.setState({seventh: e.target.value})}
        />
        <br/>
        <Ninth
        value={this.state.ninth}
        change={(e) => this.setState({ninth: e.target.value})}
        />
        <br/>
        <Eleventh
        value={this.state.eleventh}
        change={(e) => this.setState({eleventh: e.target.value})}
        />
        <br/>
        <Thirteenth
        value={this.state.thirteenth}
        change={this.changeThirteenth}
        />
        <br/>
        <div onClick={() => console.log(this.state)}>Log State</div>
        <br/>
        <button onClick={() => Trytune.play(Trytune.updateAudio(this.state), this.state.playtime, this.state.shape)}>Play</button>
      </div>
    )
  }
}

function PlayTime(props) {
  return (
    <div>
    Play Time (s):
    <input type="text" name="PlayTime"
    defaultValue={props.value} onChange={props.change}/>
    </div>
  )
}

function Fundamental(props) {
  return (
    <div>
    Fundamental (Hz):
    <input type="text" name="Fundamental"
    defaultValue={props.value} onChange={props.change}/>
    </div>
  )
}

function Shape(props) {
  return (
    <div>
    Shape:
    <select defaultValue={props.value} onChange={props.change}>
      <option value='sine'>sine</option>
      <option value='triangle'>triangle</option>
      <option value='saw'>saw</option>
      <option value='square'>square</option>
    </select>
    </div>
  )
}

function Root(props) {
  return (
    <div>
    Root:
    <select defaultValue={props.value} onChange={props.change}>
      <option value='0'>I</option>
      <option value='2'>II</option>
      <option value='4'>III</option>
      <option value='5'>IV</option>
      <option value='7'>V</option>
      <option value='9'>VI</option>
      <option value='11'>VII</option>
    </select>
    </div>
  )
}

function RootBNS(props) {
  return (
    <div>
      BNS:
      <label>b
        <input type="radio" value='-1'
        checked={props.value === '-1'}
        onChange={props.change}/>
      </label>
      <label>n
        <input type="radio" value='0'
        checked={props.value === '0'}
        onChange={props.change}/>
      </label>
      <label>s
        <input type="radio" value='1'
        checked={props.value === '1'}
        onChange={props.change}/>
      </label>
    </div>
  )
}

function Triad(props) {
  return (
    <div>
      Triad:
      <select defaultValue={props.value} onChange={props.change}>
        <option value='[4,7]'>Major</option>
        <option value='[3,7]'>Minor</option>
        <option value='[4,8]'>Augmented</option>
        <option value='[3,6]'>Diminished</option>
        <option value='[5,7]'>Sus4</option>
        <option value='[2,7]'>Sus2</option>
      </select>
    </div>
  )
}

